rule.example:

class FooController
{
    public function barAction() // good
    {
    }

    public function doSomthing() // bad
    {
    }
}


class Entity
{
    private $data;

    public function getData() // good
    {
        return $this->data;
    }

    public function setData($data) // good
    {
        $this->data = $data;

        return $this;
    }

    public function setData($data) // bad
    {
        $this->data = $data;
        $this->doSomething();
    }

    public function doSomthing() // bad
    {
        foreach ($this->data as $data) {
            calculate($data);
        }
    }
}
